import { isIterable } from "@thi.ng/checks";
import { compR } from "../func/compr";
import { iterator1 } from "../iterator";
export function benchmark(src) {
    return isIterable(src)
        ? iterator1(benchmark(), src)
        : (rfn) => {
            const r = rfn[2];
            let prev = Date.now();
            return compR(rfn, (acc, _) => {
                const t = Date.now();
                const x = t - prev;
                prev = t;
                return r(acc, x);
            });
        };
}
